mapper.select:
SELECT * FROM ( SELECT U.USERID, U.NAME, U.BIRTH, U.GENDER, U.NICKNAME, U.PHONE1, U.PHONE2, U.PHONE3, U.LOGINCNT, U.USER_GRADE, U.USERTYPE, U.EMAIL, U.TOTALPAY, U.POINT,
@row_number:=@row_number + 1 AS PAGE FROM (SELECT @row_number:=0) AS t, ECO_USER U WHERE 1=1
AND USERID LIKE CONCAT('%', #{keyword}, '%')
AND NAME LIKE CONCAT('%', #{keyword}, '%')
) AS subquery WHERE PAGE BETWEEN #{startNum} AND #{lastNum}
