mapper.select:
SELECT B.*, DATE_FORMAT(CDATETIME, '%Y년 %c월 %e일 %H시 %i분') AS CDATE FROM ECO_BOARD B WHERE KIND =#{kind}
AND TITLE LIKE CONCAT('%',#{keyword},'%')
AND USERID LIKE CONCAT('%',#{keyword},'%')
ORDER BY CDATETIME DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM ECO_BOARD B INNER JOIN ECO_USER U ON B.USERID = U.USERID WHERE KIND = #{kind}
AND TITLE LIKE CONCAT('%',#{keyword},'%')
AND B.USERID LIKE CONCAT('%',#{keyword},'%')
