mapper.update:
UPDATE ECO_CART
SET
	SELECTCNT = #{selectCnt}
WHERE CART_NO = #{cartNo}

UPDATE ECO_USER
SET
	POINT = POINT - #{usePoint} + #{rewardPoint},
	TOTALPAY = TOTALPAY + #{totalPay}
WHERE USERID = #{userId}

UPDATE ECO_PRODUCT
SET
	CNT = CNT - #{selectCnt},
	SELLCNT = SELLCNT + #{selectCnt}
WHERE ITEM_NO = #{itemNo}
